INSERT INTO `article` (`id`, `category_id`, `title`, `link`, `content`, `url_thumbnail`, `created_at`, `view`, `latitude`, `longitude`, `locationDetail`) VALUES
(7, 1, '5 công trình bị bỏ hoang tại Việt Nam, muốn có những bức ảnh kì bí \"hết cả hồn\" thì nhất định phải đến!', '', '<p><span style=\"\">Chúng ta đã từng nghe, từng xem những câu chuyện, những hình ảnh về biết bao địa điểm bị bỏ hoang ở khắp nơi trên thế giới. Đấy là còn chưa kể, trong phim ảnh kinh dị, có bao giờ bạn nhìn nhân vật lạc vào một nơi hoang tàn rồi tưởng tượng nếu là mình thì mình sẽ như thế nào không. Nếu bạn tò mò thì sao không thử một c</span><span style=\"\">huyến hành trình <a title=\" Du lịch\" class=\"link-tag\" target=\"_blank\" href=\"http://kenh14.vn/doi-song/du-lich.chn\">du lịch</a> khám phá những địa điểm ly kỳ như vậy nhỉ, những nơi đó thực sự kích thích đối với ai ưa mạo hiểm, và dĩ nhiên, không dành cho người yếu tim bao giờ.</span></p><p><span style=\"\">Ở Việt Nam cũng có những nơi bị bỏ hoang đã lâu, tất cả những địa điểm đó đều đã từng rất đông người qua lại, nhưng vì một lý do nào đó chẳng ai biết, chỉ biết là giờ không ai quan tâm đến chúng nữa và cứ để mặc thời gian, thiên nhiên phủ lớp bụi mờ ảo lên chúng. Rất nhiều những câu chuyện giải thích cho sự hoang tàn, đổ nát được tô vẽ, không biết sự thật chiếm bao nhiêu phần trăm, nhưng độ rùng rợn thì quả thực 100%.</span></p><p><span style=\"\">Bạn có dám đặt chân tới 6 địa điểm bị bỏ hoang ở Việt Nam mình dưới đây không. Thật sự không nói giỡn khi bước chân vào những nơi này, cảm giác đầu tiên là lạnh lẽo và kỳ quái. Nhưng, chính vẻ đổ nát của chúng lại làm nên một vẻ đẹp bất ngờ đấy nhé:</span></p><h3><span style=\"\"><b>1. Nhà nguyện Đà Lạt</b></span></h3><p>Bất cứ ai trong chúng ta khi được hỏi: \"Nơi nào bí ẩn nhất ở Việt Nam\" thì đảm bảo là Đà Lạt sẽ được gọi tên đầu tiên.</p><p>Với những căn biệt thự bỏ hoang trên đồi, rồi khí hậu lại lạnh lẽo, mặt trời cứ buông xuống là sương mờ cũng sà sát đất luôn. Thành ra, từ lâu Đà Lạt đã luôn khiến người ta \"rùng mình\" bởi những điều như vậy đó. Đố ai dám vào những căn nhà đó từ lúc trời nhá nhem đấy.</p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><a href=\"https://kenh14cdn.com/2017/mg-0930-1-1502878266049.jpg\" data-fancybox-group=\"img-lightbox\" title=\"\" target=\"_blank\" class=\"detail-img-lightbox\"><img src=\"https://kenh14cdn.com/2017/mg-0930-1-1502878266049.jpg\" id=\"img_46f0fec0-826b-11e7-b476-bf77562fd1bf\" w=\"960\" h=\"640\" alt=\"5 công trình bị bỏ hoang tại Việt Nam, muốn có những bức ảnh kì bí hết cả hồn thì nhất định phải đến! - Ảnh 1.\" title=\"5 công trình bị bỏ hoang tại Việt Nam, muốn có những bức ảnh kì bí hết cả hồn thì nhất định phải đến! - Ảnh 1.\" rel=\"lightbox\" photoid=\"46f0fec0-826b-11e7-b476-bf77562fd1bf\" type=\"photo\" style=\"width:100%;max-width:100%;\" data-original=\"https://kenh14cdn.com/2017/mg-0930-1-1502878266049.jpg\" width=\"\" height=\"\" class=\"lightbox-content\"></a></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><a href=\"https://kenh14cdn.com/2017/mg-0911-1502878266048.jpg\" data-fancybox-group=\"img-lightbox\" title=\"\" target=\"_blank\" class=\"detail-img-lightbox\"><img src=\"https://kenh14cdn.com/2017/mg-0911-1502878266048.jpg\" id=\"img_45b39cc0-826b-11e7-9937-2f2507d19b68\" w=\"960\" h=\"640\" alt=\"5 công trình bị bỏ hoang tại Việt Nam, muốn có những bức ảnh kì bí hết cả hồn thì nhất định phải đến! - Ảnh 2.\" title=\"5 công trình bị bỏ hoang tại Việt Nam, muốn có những bức ảnh kì bí hết cả hồn thì nhất định phải đến! - Ảnh 2.\" rel=\"lightbox\" photoid=\"45b39cc0-826b-11e7-9937-2f2507d19b68\" type=\"photo\" style=\"width:100%;max-width:100%;\" data-original=\"https://kenh14cdn.com/2017/mg-0911-1502878266048.jpg\" width=\"\" height=\"\" class=\"lightbox-content\"></a></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><div class=\"VCSortableInPreviewMode noCaption\" type=\"P