INSERT INTO `article` (`id`, `category_id`, `title`, `link`, `content`, `url_thumbnail`, `created_at`, `view`, `latitude`, `longitude`, `locationDetail`) VALUES
(8, 1, '5 resort sang chảnh nào tại Việt Nam được hội \"Rich Kid\" ưa thích nhất?', '', '<p>Để khám phá vẻ đẹp của một vùng đất, thực chất có rất nhiều cách khác nhau. Bạn có thể thực hiện những hành trình khám phá một vùng đất \"khó nhằn\", cảm giác vượt qua mọi vất vả để đạt tới điều mà mình muốn thật thú vị. Nhưng bên cạnh đó, bạn cũng có thể chọn cho mình cách tận hưởng chuyến đi bằng sự nghỉ dưỡng, tiện nghi.</p><p>Ai bảo là kiểu du lịch nghỉ dưỡng là nhàm chán, là chẳng có gì thú vị nào. Sự thực là tất cả những nơi được xây dựng resort đều là những địa điểm có cảnh quan, khí hậu...hết sức lý tưởng đấy nhé. Cảnh vật thiên nhiên hoang sơ có những sự thú vị, nhưng cảnh vật thiên nhiên ở bên sự hiện đại cũng hấp dẫn chẳng kém chút nào.</p><p>Nếu bạn không phải một người thích du lịch theo kiểu \"bụi\", kiểu \"phượt\". Việc lên rừng xuống biển, lên thác xuống ghềnh có lẽ không hợp với bạn, thì cũng chẳng sao cả. Bạn hoàn toàn có thể lựa chọn việc thư thái, an nhàn trong khu resort với thiết kế siêu đẹp và ấn tượng. Dưới đây là những khu resort sang chảnh được hội \"rich kid\" và giới sao Việt check-in rất nhiều. Chỉ cần đặt chân tới đây thôi là bạn sẽ cảm thấy thật sự đáng đồng tiền lắm.</p><h3 style=\"text-align: center;\"><span style=\"\"><b>1. Amanoi Ninh Thuận</b></span></h3><p><span style=\"\">Nếu bạn có dịp đi tham quan vịnh Vĩnh Hy, từ trên tàu, bạn sẽ thấy một khu resort sang chảnh, yên tĩnh nằm ở địa thế tuyệt đẹp trên đỉnh vách núi, nhìn ra khung cảnh thiên nhiên thơ mộng.</span></p><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><a href=\"https://kenh14cdn.com/2017/thehoteledit-1462539953369-1502278181415.png\" data-fancybox-group=\"img-lightbox\" title=\"\" target=\"_blank\" class=\"detail-img-lightbox\"><img src=\"https://kenh14cdn.com/2017/thehoteledit-1462539953369-1502278181415.png\" id=\"img_1b8d8cf0-7cf6-11e7-bd4f-31dd9d9a03af\" w=\"620\" h=\"562\" alt=\"5 resort sang chảnh nào tại Việt Nam được hội Rich Kid ưa thích nhất? - Ảnh 1.\" title=\"5 resort sang chảnh nào tại Việt Nam được hội Rich Kid ưa thích nhất? - Ảnh 1.\" rel=\"lightbox\" photoid=\"1b8d8cf0-7cf6-11e7-bd4f-31dd9d9a03af\" type=\"photo\" style=\"width:100%;max-width:100%;\" data-original=\"https://kenh14cdn.com/2017/thehoteledit-1462539953369-1502278181415.png\" width=\"\" height=\"\" class=\"lightbox-content\"></a></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><div class=\"VCSortableInPreviewMode noCaption\" type=\"Photo\" style=\"\"><div><a href=\"https://kenh14cdn.com/2017/screenshot-2016-05-06-14-46-15-1462540218388-1502278181413.png\" data-fancybox-group=\"img-lightbox\" title=\"\" target=\"_blank\" class=\"detail-img-lightbox\"><img src=\"https://kenh14cdn.com/2017/screenshot-2016-05-06-14-46-15-1462540218388-1502278181413.png\" id=\"img_1b2d92a0-7cf6-11e7-bd4f-31dd9d9a03af\" w=\"620\" h=\"602\" alt=\"5 resort sang chảnh nào tại Việt Nam được hội Rich Kid ưa thích nhất? - Ảnh 2.\" title=\"5 resort sang chảnh nào tại Việt Nam được hội Rich Kid ưa thích nhất? - Ảnh 2.\" rel=\"lightbox\" photoid=\"1b2d92a0-7cf6-11e7-bd4f-31dd9d9a03af\" type=\"photo\" style=\"width:100%;max-width:100%;\" data-original=\"https://kenh14cdn.com/2017/screenshot-2016-05-06-14-46-15-1462540218388-1502278181413.png\" width=\"\" height=\"\" class=\"lightbox-content\"></a></div><div class=\"PhotoCMS_Caption\"><p data-placeholder=\"[nhập chú thích]\" class=\"NLPlaceholderShow\"></p></div></div><div class=\"VCSortableInPreviewMode\" type=\"Photo\" style=\"\"><div><a href=\"https://kenh14cdn.com/2017/photo-5-1462587802921-1502278181411.png\" data-fancybox-group=\"img-lightbox\" title=\"Nhà thiết kế Adrian Anh Tuấn cũng đã có những trải nghiệm tuyệt vời tại đây\" target=\"_blank\" class=\"detail-img-lightbox\"><img src=\"https://kenh14cdn.com/2017/photo-5-1462587802921-1502278181411.png\" id=\"img_1aab6a50-7cf6-11e7-bd4f-31dd9d9a03af\" w=\"620\" h=\"437\" alt=\"5 res